mapper.select:
select * from exam


		and name like '%${name}%'


		and subjectId = #{subjectId}


		and startTime >= #{startTime}


		and endTime <= #{endTime}


		and subjectId in

			#{subject.id}




	limit #{offset},#{pageSize}

select * from exam where 1 = 1

	and name like '%${name}%' 


	and subjectId = #{subjectId}  


	and startTime <= #{startTime}  


	and endTime >= #{endTime}  

order by createTime desc 

	limit #{offset},#{pageSize}

select count(*) from exam


		and name like '%${name}%'


		and subjectId = #{subjectId}


		and startTime <= #{startTime}


		and endTime >= #{endTime}

select count(*) from exam


		and name like '%${name}%'


		and subjectId = #{subjectId}


		and startTime >= #{startTime}


		and endTime <= #{endTime}


		and subjectId in

			#{subject.id}
